import React, { useState, useEffect } from "react";
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import { Link } from "react-router-dom";
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from "../layout/Navbar";

const AddDroppingPoint = () => {
  const [droppingPoints, setDroppingPoint] = useState([]);
  const history = useHistory();
  const routeId = sessionStorage.getItem("routeId");

  useEffect(() => {
    getDroppingPoints();
  }, []);

  const getDroppingPoints = () => {
    axios.get('http://localhost:8080/route/getdroppingpoint/' + routeId).then((response) => {
      const result = response.data;
      if (result.status === 'success') {
        setDroppingPoint(result.data);
      } else {
        Swal.fire('Error', 'Error while loading list of Routes', 'error');
      }
    });
  };

  const initialValues = {
    name: ''
  };

  const validationSchema = Yup.object().shape({
    name: Yup.string().required('Station name is required')
  });

  const onSubmit = (values) => {
    const data = new FormData();
    data.append('name', values.name);

    axios.post('http://localhost:8080/route/adddroppingpoint/' + routeId, data).then((response) => {
      const result = response.data;
      if (result.status === 'success') {
        Swal.fire('Success', 'Successfully added station', 'success');
        history.push('/routelist');
      } else {
        Swal.fire('Error', 'Error while adding station', 'error');
      }
    });
  };

  return (
    <div>
      <Navbar />
      <div className="container mt-5">
        <div className="card mb-4 w-50 mx-auto">
          <div className="card-header  text-white text-center" style={{backgroundColor:"#7e22ce"}}>
            <h3>Add New Dropping Point</h3>
          </div>
          <div className="card-body">
            <Formik
              initialValues={initialValues}
              validationSchema={validationSchema}
              onSubmit={onSubmit}
            >
              <Form>
                <div className="form-group">
                  <label htmlFor="name">Name</label>
                  <Field
                    type="text"
                    name="name"
                    className="form-control"
                    placeholder="Enter Station Name"
                  />
                  <ErrorMessage name="name" component="div" className="text-danger" />
                </div>
                <button type="submit" className="btn btn-success mt-3">Confirm</button>
                <Link to="/routelist" className="btn btn-warning mt-3 ms-3">Back</Link>
              </Form>
            </Formik>
          </div>
        </div>

        <div className="card  w-50 mx-auto">
          <div className="card-header  text-white text-center" style={{backgroundColor:"#7e22ce"}}>
            <h3>Dropping Point List</h3>
          </div>
          <div className="card-body">
            <table className="table table-striped table-hover">
              <thead style={{backgroundColor:"#ae77df"}}>
                <tr>
                  <th>Id</th>
                  <th>Name</th>
                </tr>
              </thead>
              <tbody>
                {droppingPoints.map((point) => (
                  <tr key={point.id}>
                    <td>{point.id}</td>
                    <td>{point.name}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddDroppingPoint;
